mapper.update:
UPDATE t_account_info set balance = balance - frozen_balance + #{accountBalanceBO.amount}, out_reserve_balance = out_reserve_balance - frozen_balance + #{accountBalanceBO.amount}, version = version +1, account_his_serial = account_his_serial +1
,yesterday_balance = #{accountBalanceBO.yesterdayBalance,jdbcType=DECIMAL}
,last_trad_time = #{accountBalanceBO.lastTradTime,jdbcType=DATE}
WHERE account_no = #{accountBalanceBO.accountNo} and balance - frozen_balance >= #{accountBalanceBO.amount} and version = #{accountBalanceBO.version}

UPDATE t_account_info set balance = balance - frozen_balance + #{accountBalanceBO.amount}, out_reserve_balance = out_reserve_balance - frozen_balance + #{accountBalanceBO.amount}, version = version + 1, account_his_serial = account_his_serial +1
,yesterday_balance = #{accountBalanceBO.yesterdayBalance,jdbcType=DECIMAL}
,last_trad_time = #{accountBalanceBO.lastTradTime,jdbcType=DATE}
WHERE account_no = #{accountBalanceBO.accountNo} and balance - frozen_balance + #{accountBalanceBO.amount} >= 0 and out_reserve_balance - frozen_balance + #{accountBalanceBO.amount} >= 0 and version = #{accountBalanceBO.version}

UPDATE t_account_info set balance = balance + #{accountBalanceBO.amount}, version = version +1, account_his_serial = account_his_serial +1
,yesterday_balance = #{accountBalanceBO.yesterdayBalance,jdbcType=DECIMAL}
,last_trad_time = #{accountBalanceBO.lastTradTime,jdbcType=DATE}
WHERE account_no = #{accountBalanceBO.accountNo} and version = #{accountBalanceBO.version}
